import React, { useEffect, useState, useRef } from "react";
import Comment from "./Comment"
import { Route } from 'react-router-dom';
import { axiosWithAuth } from '../../utils/axiosWithAuth'

import './CommentFeed.css'
import { TimelineLite } from 'gsap'






function CommentFeed() {

    const [ comments, addComments ] = useState([])

    let myElements = []
    let myTween = new TimelineLite({paused: true});

    useEffect(() => {
        myTween = TweenLite.staggerto(myElements, 0.5, {y: 0, autoAlpha: 1}, 0.1);
    }, []);

    //get comments 
        useEffect(() => { 
            axiosWithAuth().get(`https://saltyhackers2.herokuapp.com/users/1/AllComments`)
            .then(res => {
                console.log('this should only render once!')
                console.log(res)
                addComments(res.data)
                console.log(comments)
            })
            .catch(err => {
                console.log('nope')
                })
            }, [])

    return (
        <Route path='/feed'>
            <div className='feed-container'>
                <div className='feed-list'>

                    {
                        comments.map((comment, index) => {
                        return <Comment key={comment.id} info={comment} ref={el => myElements[index] = el}/>
                        })

                    }

                </div>
            </div>

        </Route>

    )
}


export default CommentFeed
