import React from 'react'
import {
    Card, CardBody,
    CardTitle, CardSubtitle, Button, CardImg
  } from 'reactstrap';

  import image from './blank-profile-picture.png';


export default function Comment (props) {

    const { info, saveComment } = props

    // console.log(props, '<-- Comment consolelog')
    return (


    <Card style={{ backgroundColor: '#f6f6ef', width: '25rem', margin: '1%'}}>
            <CardImg src={image}></CardImg>
            <CardBody style= {{color: 'black'}}>
                <CardTitle style={{fontWeight: 'bold'}}>Username: {info.UserName}</CardTitle>
                <CardSubtitle style={{color: 'red' }}>Salty Score: {info.Saltiness}</CardSubtitle>
                <CardSubtitle>Comment: {info.Comment}</CardSubtitle>
                <Button onClick={() => saveComment(info.Comment_ID)}>Save Comment</Button>
            </CardBody>
    </Card> 

    )
}
