import React, {useRef, useEffect} from 'react'
import {
    Card, CardBody,
    CardTitle, CardSubtitle, CardDeck, CardText, CardImg, CardGroup
  } from 'reactstrap';

import {TweenMax, Power3} from 'gsap'





export default function Comment (props) {

    const { info } = props

    let cardItem = useRef(null)

    useEffect(() => {
        TweenMax.to(
            cardItem,
            .8,
            {
                y: 100,
                ease
            }
        )



    })

    return (

    <CardGroup>

        <Card ref={el => { cardItem = el }} style={{ backgroundColor: '#f6f6ef', borderRadius: '25px', 
            margin: '1%', display: 'flex', flexDirection:'row'}}>

                <CardBody inverse style= {{color: 'black'}}>
                    {/* <CardImg src="/images/blank-profile-picture.png"/> */}
                        <CardTitle style={{fontWeight: 'bold'}}>Username: {info.UserName}</CardTitle>
                        <CardSubtitle style={{fontWeight: 'bold'}}>Salty Score: {info.SaltyScore}</CardSubtitle>
                        <CardText>Comment: {info.Comment}</CardText>

                    </CardBody>          

        </Card>

    </CardGroup>




    )


}
