import React, { useEffect, useState, useRef } from "react";
import Comment from "./Comment"
import { Route } from 'react-router-dom';
import { axiosWithAuth } from '../../utils/axiosWithAuth'

import './CommentFeed.css'

//import { TimelineLite, TweenLite } from 'gsap'







function CommentFeed() {

    const [ comments, addComments ] = useState([])


    let myElement = useRef();
    //let myTween = new TimelineLite({paused: true});

    //useEffect(() => {
        //myTween = TweenLite.staggerTo(myElement, 0.5, {y: 0, autoAlpha: 1}, 0.1);
        //console.log('it ran!')
    //}, []);




    //get comments 
        useEffect(() => { 
            axiosWithAuth().get(`https://saltyhackers2.herokuapp.com/users/1/AllComments`)
            .then(res => {
                console.log('this should only render once!')
                console.log(res)
                addComments(res.data)
                console.log(comments)
            })
            .catch(err => {
                console.log('nope')
                })
            }, [])

    return (
        <Route path='/feed'>
            <div className='feed-container'>

            <div ref={myElement}>
                        Can I animate this?
                    </div>
                <div className='feed-list'>

                    {
                        comments.map(comment  => {
                        return <Comment key={comment.id} info={comment} />
                        })

                    }



                </div>
            </div>

        </Route>

    )
}


export default CommentFeed
